$(document).ready(function(){var a={init:function(){this.buttons=$("#main-nav a");this.activateNav();this.disableDemoButtons()},activateNav:function(){var b=this;this.buttons.click(function(f){f.preventDefault();var c=$(f.currentTarget);var d=c.attr("href");b.buttons.parent().removeClass("selected");c.parent().addClass("selected");$("html, body").animate({scrollTop:$(d).offset().top-100},"slow")})},disableDemoButtons:function(){$(".showcase [href^=#]").on("click",function(b){b.preventDefault()})}};a.init()});