$(document).ready(function(){$(".filterable .btn-filter").click(function(){var c=$(this).parents(".filterable"),b=c.find(".filters input"),a=c.find(".table tbody");if(b.prop("disabled")==true){b.prop("disabled",false);b.first().focus()}else{b.val("").prop("disabled",true);a.find(".no-result").remove();a.find("tr").show()}});$(".filterable .filters input").keyup(function(d){var b=d.keyCode||d.which;if(b=="9"){return}var g=$(this),a=g.val().toLowerCase(),j=g.parents(".filterable"),c=j.find(".filters th").index(g.parents("th")),i=j.find(".table"),h=i.find("tbody tr");var f=h.filter(function(){var e=$(this).find("td").eq(c).text().toLowerCase();return e.indexOf(a)===-1});i.find("tbody .no-result").remove();h.show();f.hide();if(f.length===h.length){i.find("tbody").prepend($('<tr class="no-result text-center"><td colspan="'+i.find(".filters th").length+'">No result found</td></tr>'))}})});