function format(a){return'<table cellpadding="5" cellspacing="0" border="0" style="padding-left:50px;"><tr><td>Full name:</td><td>'+a.name+"</td></tr><tr><td>Extension number:</td><td>"+a.extn+"</td></tr><tr><td>Extra info:</td><td>And any further details here (images etc)...</td></tr></table>"}$(document).ready(function(){var a=$("#table2").DataTable({ajax:"datatabledata.txt",columns:[{"class":"details-control",orderable:false,data:null,defaultContent:""},{data:"name"},{data:"position"},{data:"office"},{data:"salary"}],order:[[1,"asc"]]});$("#table2 tbody").on("click","td.details-control",function(){var b=$(this).parents("tr");var c=a.row(b);if(c.child.isShown()){c.child.hide();b.removeClass("shown")}else{c.child(format(c.data())).show();b.addClass("shown")}})});