$(function(){module("bootstrap-collapse");test("should provide no conflict",function(){var a=$.fn.collapse.noConflict();ok(!$.fn.collapse,"collapse was set back to undefined (org value)");$.fn.collapse=a});test("should be defined on jquery object",function(){ok($(document.body).collapse,"collapse method is defined")});test("should return element",function(){ok($(document.body).collapse()[0]==document.body,"document.body returned")});test("should show a collapsed element",function(){var a=$('<div class="collapse"></div>').collapse("show");ok(a.hasClass("in"),"has class in");ok(/height/.test(a.attr("style")),"has height set")});test("should hide a collapsed element",function(){var a=$('<div class="collapse"></div>').collapse("hide");ok(!a.hasClass("in"),"does not have class in");ok(/height/.test(a.attr("style")),"has height set")});test("should not fire shown when show is prevented",function(){$.support.transition=false;stop();$('<div class="collapse"/>').bind("show",function(a){a.preventDefault();ok(true);start()}).bind("shown",function(){ok(false)}).collapse("show")});test("should reset style to auto after finishing opening collapse",function(){$.support.transition=false;stop();$('<div class="collapse" style="height: 0px"/>').bind("show",function(){ok(this.style.height=="0px")}).bind("shown",function(){ok(this.style.height=="auto");start()}).collapse("show")});test("should add active class to target when collapse shown",function(){$.support.transition=false;stop();var b=$('<a data-toggle="collapse" href="#test1"></a>').appendTo($("#qunit-fixture"));var a=$('<div id="test1"></div>').appendTo($("#qunit-fixture")).on("show",function(){ok(!b.hasClass("collapsed"));start()});b.click()});test("should remove active class to target when collapse hidden",function(){$.support.transition=false;stop();var b=$('<a data-toggle="collapse" href="#test1"></a>').appendTo($("#qunit-fixture"));var a=$('<div id="test1" class="in"></div>').appendTo($("#qunit-fixture")).on("hide",function(){ok(b.hasClass("collapsed"));start()});b.click()})});