$(function(){module("bootstrap-typeahead");test("should provide no conflict",function(){var a=$.fn.typeahead.noConflict();ok(!$.fn.typeahead,"typeahead was set back to undefined (org value)");$.fn.typeahead=a});test("should be defined on jquery object",function(){ok($(document.body).typeahead,"alert method is defined")});test("should return element",function(){ok($(document.body).typeahead()[0]==document.body,"document.body returned")});test("should listen to an input",function(){var a=$("<input />");a.typeahead();ok($._data(a[0],"events").blur,"has a blur event");ok($._data(a[0],"events").keypress,"has a keypress event");ok($._data(a[0],"events").keyup,"has a keyup event")});test("should create a menu",function(){var a=$("<input />");ok(a.typeahead().data("typeahead").$menu,"has a menu")});test("should listen to the menu",function(){var b=$("<input />"),a=b.typeahead().data("typeahead").$menu;ok($._data(a[0],"events").mouseover,"has a mouseover(pseudo: mouseenter)");ok($._data(a[0],"events").click,"has a click")});test("should show menu when query entered",function(){var b=$("<input />").appendTo("body").typeahead({source:["aa","ab","ac"]}),a=b.data("typeahead");b.val("a");a.lookup();ok(a.$menu.is(":visible"),"typeahead is visible");equals(a.$menu.find("li").length,3,"has 3 items in menu");equals(a.$menu.find(".active").length,1,"one item is active");b.remove();a.$menu.remove()});test("should accept data source via synchronous function",function(){var b=$("<input />").typeahead({source:function(){return["aa","ab","ac"]}}).appendTo("body"),a=b.data("typeahead");b.val("a");a.lookup();ok(a.$menu.is(":visible"),"typeahead is visible");equals(a.$menu.find("li").length,3,"has 3 items in menu");equals(a.$menu.find(".active").length,1,"one item is active");b.remove();a.$menu.remove()});test("should accept data source via asynchronous function",function(){var b=$("<input />").typeahead({source:function(c,d){d(["aa","ab","ac"])}}).appendTo("body"),a=b.data("typeahead");b.val("a");a.lookup();ok(a.$menu.is(":visible"),"typeahead is visible");equals(a.$menu.find("li").length,3,"has 3 items in menu");equals(a.$menu.find(".active").length,1,"one item is active");b.remove();a.$menu.remove()});test("should not explode when regex chars are entered",function(){var b=$("<input />").typeahead({source:["aa","ab","ac","mdo*","fat+"]}).appendTo("body"),a=b.data("typeahead");b.val("+");a.lookup();ok(a.$menu.is(":visible"),"typeahead is visible");equals(a.$menu.find("li").length,1,"has 1 item in menu");equals(a.$menu.find(".active").length,1,"one item is active");b.remove();a.$menu.remove()});test("should hide menu when query entered",function(){stop();var b=$("<input />").typeahead({source:["aa","ab","ac"]}).appendTo("body"),a=b.data("typeahead");b.val("a");a.lookup();ok(a.$menu.is(":visible"),"typeahead is visible");equals(a.$menu.find("li").length,3,"has 3 items in menu");equals(a.$menu.find(".active").length,1,"one item is active");b.blur();setTimeout(function(){ok(!a.$menu.is(":visible"),"typeahead is no longer visible");start()},200);b.remove();a.$menu.remove()});test("should set next item when down arrow is pressed",function(){var b=$("<input />").typeahead({source:["aa","ab","ac"]}).appendTo("body"),a=b.data("typeahead");b.val("a");a.lookup();ok(a.$menu.is(":visible"),"typeahead is visible");equals(a.$menu.find("li").length,3,"has 3 items in menu");equals(a.$menu.find(".active").length,1,"one item is active");ok(a.$menu.find("li").first().hasClass("active"),"first item is active");b.trigger({type:"keydown",keyCode:40}).trigger({type:"keypress",keyCode:40}).trigger({type:"keyup",keyCode:40});ok(a.$menu.find("li").first().next().hasClass("active"),"second item is active");b.trigger({type:"keydown",keyCode:38}).trigger({type:"keypress",keyCode:38}).trigger({type:"keyup",keyCode:38});ok(a.$menu.find("li").first().hasClass("active"),"first item is active");b.remove();a.$menu.remove()});test("should set input value to selected item",function(){var e=$("<input />").typeahead({source:["aa","ab","ac"]}).appendTo("body"),b=e.data("typeahead"),d=false,a=false,c=false;e.val("a");b.lookup();e.change(function(){d=true});e.focus(function(){a=true;c=false});e.blur(function(){c=true;a=false});$(b.$menu.find("li")[2]).mouseover().click();equals(e.val(),"ac","input value was correctly set");ok(!b.$menu.is(":visible"),"the menu was hidden");ok(d,"a change event was fired");ok(a&&!c,"focus is still set");e.remove();b.$menu.remove()});test("should start querying when minLength is met",function(){var b=$("<input />").typeahead({source:["aaaa","aaab","aaac"],minLength:3}).appendTo("body"),a=b.data("typeahead");b.val("aa");a.lookup();equals(a.$menu.find("li").length,0,"has 0 items in menu");b.val("aaa");a.lookup();equals(a.$menu.find("li").length,3,"has 3 items in menu");b.remove();a.$menu.remove()})});