$(document).ready(function(){$(document).on("click",".panel-heading .removepanel",function(){var a=$(this);a.parents(".panel").hide("slow")});$(".showhide").attr("title","Hide Panel content");$(document).on("click",".panel-heading .clickable",function(b){var a=$(this);if(!a.hasClass("panel-collapsed")){a.parents(".panel").find(".panel-body").slideUp();a.addClass("panel-collapsed");a.find("i").removeClass("glyphicon-chevron-up").addClass("glyphicon-chevron-down");$(".showhide").attr("title","Show Panel content")}else{a.parents(".panel").find(".panel-body").slideDown();a.removeClass("panel-collapsed");a.find("i").removeClass("glyphicon-chevron-down").addClass("glyphicon-chevron-up");$(".showhide").attr("title","Hide Panel content")}})});